declare module '*.svg' {
  import React = require('react');
  export const ReactComponent: React.SFC<React.SVGProps<SVGSVGElement>>;
  const src: string;
  export default src;
}

declare module '*.png' {
  export default content;
}
declare module '*.jpg' {
  export default content;
}

declare module '*.gif';

